mapper.select:
SELECT *
FROM PROPERTY_TBL
WHERE PROPERTY_NO = #{id, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO PROPERTIES_IMG_TBL SELECT SEQ_PROPERTIES_IMG_NO.NEXTVAL, BI.* FROM (
SELECT #{img.propertyNo, jdbcType=NUMERIC} AS PROPERTY_NO, #{img.imgPath, jdbcType=VARCHAR} AS IMG_PATH, #{img.imgRename, jdbcType=VARCHAR} AS IMG_RENAME, #{img.imgOriginalName,jdbcType=VARCHAR} AS IMG_ORIGINAL_NAME, #{img.imgOrder, jdbcType=NUMERIC} AS IMG_ORDER FROM DUAL
) BI
